export default Snooze;

const Section = styled.section`
  text-align: center;
  background-color: rgba(249, 232, 204, 0.7);
  box-shadow: 0 10px 10px -10px rgba(0, 0, 0, 0.3);
  padding: 1.5rem 2.5rem;
  border-radius: 20%;
  /* width: 20%; */
  /* height: 50px; */
  margin: auto;

  .snooze {
    width: auto;
    /* text-align: center; */
    font-size: 2.5rem;
    color: #343f56;
    border-radius: 20%;
    display: inline;
  }
`;
