import React from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import styled from "styled-components";

const Msg = () => (
  <div>
    Rub your hands and gently place your warm hands over your eyes, with fingertips on the forehead.
  </div>
)


function Notification() {

    const displayMsg = () => {
    toast(<Msg />) 
  }

    const closeButton = ({ closeToast }) => {
      <button onClick={closeToast}></button>
    }

    const ColoredLine = ({ color }) => (
  <hr
    style={{
      color,
      backgroundColor: color,
      height: 5
    }}
  />
);







  return (
    <Section>
      <div clasName="row">
        <div  className="column left">

        </div>


      <div className="column right">
      <button className="noti" onClick={displayMsg}>Accept</button>
      <ColoredLine color="red" />
     <button className="noti" onClick={closeButton}>Close</button>
      </div>

     </div>
    </Section>

  )
}

export default Notification

const Section = styled.section`
  .row:after {
  content: "";
  display: table;
  clear: both;
}

    .column {
      float: left;
    }

    .left {
      width: 75%;
    }

    .right {
      width: 25%;
    }


  .Toastify__close-button--light {
    display: none;
  }

  .noti{
    background-color: #ffffff;
    border-radius: 0%;

    font-size: 1rem;
    font-weight: 500;

    padding: 0.2rem;
    margin: 0;
  }
`;
