import React, { useState } from "react";
import styled from "styled-components";
import { RiNotificationOffLine } from "react-icons/ri";
import { RiNotificationLine } from "react-icons/ri";

const Snooze = ({setPause}) => {

    const [toggled, toggle] = useState(false);

    function what() {
      setPause(true);
      toggle(toggled => !toggled)
    }

  return (
    <Section>
      <div>
      <div className="snooze" onClick={() => {setPause(true)}}>
          <div className="App">{toggled && <><RiNotificationLine /></>}</div>
      <br />
        {/* <button onClick={pause}>Pause</button> */}
        <RiNotificationOffLine />
      </div>
      </div>
    </Section>
  );
};

// const Title = () => {
//    const [title, setTitle] = useState("Click here");

//    return <h1 onClick={() => setTitle("New title")}>{title}</h1>;
// }

export default Snooze;

const Section = styled.section`
  text-align: center;
  background-color: rgba(249, 232, 204, 0.7);
  box-shadow: 0 10px 10px -10px rgba(0, 0, 0, 0.3);
  padding: 1.5rem 2.5rem;
  border-radius: 20%;
  /* width: 20%; */
  /* height: 50px; */
  margin: auto;

  .snooze {
    width: auto;
    /* text-align: center; */
    font-size: 2.5rem;
    color: #343f56;
    border-radius: 20%;
    display: inline;
  }
`;
