import React from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import styled from "styled-components";

const Msg = () => (
  <div>
    Rub your hands and gently place your warm hands over your eyes, with fingertips on the forehead.
  </div>
)


function Notification() {

    const displayMsg = () => {
    toast(<Msg />) 
  }

    const closeButton = ({ closeToast }) => {
      <button onClick={closeToast}></button>
    }







  return (
    <Section>
      <div>
      <h4 className="column left">It is time to rest you eyes</h4>

      <div className="column right">
             <button className="noti" onClick={displayMsg}>Accept</button>
     <button className="noti" onClick={closeButton}>Close</button>
      </div>

     </div>
    </Section>

  )
}

export default Notification

const Section = styled.section`
  .row {
    display: flex;

  }

    .column {
      flex: 80%;
    }



  .Toastify__close-button--light {
    display: none;
  }

  .noti{
    background-color: #ffffff;
    border-radius: 0%;

    font-size: 1rem;
    font-weight: 500;

    padding: 0.2rem;
    margin: 0;
  }
`;
