import React, {useState} from 'react'
import styled from "styled-components";
import { RiNotificationOffLine } from "react-icons/ri";
import { RiNotificationLine } from 'react-icons/ri';



  const Snooze = () => {
    const[ on, off] = useState(<RiNotificationOffLine />);

    const snoozeToggler = () => {
    on === "light" ? setTheme("dark") : setTheme("light");
  };



  return (
      <Section>
     <div className="snooze" onClick={on}>
        {/* <button onClick={pause}>Pause</button> */}

             {off ? <RiNotificationLine /> : <RiNotificationOffLine />}


    </div>
      </Section>

  )
}

